:host {
  border-radius: 5px;
  border: none;
  font-size: 1em;
  line-height: 20px;
  font-family: inherit;
  cursor: pointer;
  letter-spacing: 0.5px;
  font-weight: bold;
  text-align: center;
  text-decoration: none;

  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }

  &.loading {
    opacity: 0.5;
    position: relative;
    cursor: default;

    &::after {
      content: '';
      position: absolute;
      top: 0;
      right: 0;
      bottom: 0;
      left: 0;
      background:url("data:image/gif;base64,") no-repeat 50% 50%;
    }
  }

  &.uppercase {
    text-transform: uppercase;
  }

  &.full-width {
    width: 100%;
    display: block;
  }

  &.not-stroked {
    &.size-sm {
      padding: 5px 16px;
    }
    &.size-md {
      padding: 10px 20px;
    }
    &.size-lg {
      padding: 15px 25px;
    }

    &.success {
      background-color: var(--success-color);
      color: white;
    }

    &.primary {
      background-color: var(--primary-color);
      color: white;
    }

    &.accend {
      background-color: var(--accend-color);
      color: white;
    }

    &.danger {
      background-color: var(--danger-color);
      color: white;
    }
  }

  &.stroked {
    background-color: transparent;

    &.size-sm {
      padding: 4px 16px;
    }
    &.size-md {
      padding: 9px 20px;
    }
    &.size-lg {
      padding: 14px 25px;
    }

    &.success {
